// MyView.swift
struct MyView: View {
    @EnvironmentObject var myViewModel : MyViewModel

    var body: some View {
        ZStack {
            if let name = myViewModel.name {
                Text("こんにちは\(name)さん")
            } else {
                Text("初めまして ゲストさん")
            }
        }
    }
}

