					u32 src = pSrc[x+i];
					u32 dst = pDst[x+i];

					u32 rbDst = dst & 0x7c1f;
					u32  gDst = dst & 0x03e0;
					u32 rbSrc = src & 0x7c1f;
					u32  gSrc = src & 0x03e0;

