	{
// type1
		for(x=0; x<212; x+=4)
		{
			for(i=0; i<4; i++)
			{
				u32 eff = pEff[x+i];

				if(eff == 0)
				{
					pVrm[x+i] = pDst[x+i];
				}
				else if(eff == 0x1F)
				{
					pVrm[x+i] = pSrc[x+i];
				}
				else
				{
					u32 src = pSrc[x+i];
					u32 dst = pDst[x+i];

					u32 rbDst = dst & 0x7c1f;
					u32  gDst = dst & 0x03e0;
					u32 rbSrc = src & 0x7c1f;
					u32  gSrc = src & 0x03e0;

					u32 rb = rbDst + (((rbSrc - rbDst) * eff) >> 5);
					u32 g  =  gDst + ((( gSrc -  gDst) * eff) >> 5);

					pVrm[x+i] = (rb & 0x7c1f) | (g & 0x03e0);
				}
			}
		}
		pSrc += 212;
		pDst += 212;
		pVrm += 240;
		pEff += 212;
