  assert.strictEqual(await page.evaluate(() => document.cookie), "")
} finally {
  // „Éá„Ç£„É¨„ÇØ„Éà„É™„ÇíÂ§ñÈÉ®„Åã„ÇâÊ∏°„Åó„ÅüÂ†¥Âêà„ÅØ Playwright „ÅåÂæåÂßãÊú´„Çí„Åó„Å¶„Åè„Çå„Å™„ÅÑ„ÅÆ„ÅßËá™ÂàÜ„Åß„Åô„Çã
  await context.close().finally(() => rm(profileDir, { recursive: true }))
}



