            if let name = myViewModel.name {
                Text("こんにちは\(name)さん")
            } else {
                Text("初めまして ゲストさん")
            }
        }
    }
}

struct MyView_Previews: PreviewProvider {
    static var previews: some View {
        MyView().environmentObject(MyViewModel())
    }
}





