class ItemsController < ApplicationController
  def new
    @item = Item.new
  end

  def create
    item = Item.create!(item_param)
    render json: {result: "ok"}
  rescue => e
    render json: {result: "ng"}
  end

  private
  def item_param
    params.require(:item).permit(:title)
  end
end





