ifネスト実装完了。やっててよかった言語処理系。すみません内容はしょぼいです。
//---------------------------------------------------------------------------
void NvPushIf(bool is)
{
	_ASSERT(Nv.ifCnt+1 < NV_MAX_IF_CNT);

	Nv.isIf[Nv.ifCnt++] = is;
}
//---------------------------------------------------------------------------
void NvPopIf(void)
{
	_ASSERT(Nv.ifCnt != 0);

	Nv.ifCnt--;
}
//---------------------------------------------------------------------------
void NvFlipIf(void)
{
	_ASSERT(Nv.ifCnt != 0);

	Nv.isIf[Nv.ifCnt-1] ^= 1;
}
//---------------------------------------------------------------------------
bool NvIsIf(void)
{
	if(Nv.ifCnt == 0)
	{
		return true;
	}

	s32 i;

	for(i=0; i<Nv.ifCnt; i++)
	{
		if(Nv.isIf[i] == false)
		{
			return false;
		}
	}

	return true;
}
