		tmp += *p[7]++ * Snd.ch[7].vol;

		// >>6 to divide off the volume
		tmp >>= 6;
		// adjust volume
		tmp >>= 1;

		// clipping
		if(tmp > 127)
		{
			tmp = 127;
		}
		else if(tmp < -127)
		{
			tmp = -127;
		}

		Snd.pBuf[i] = tmp;
