  // JSÂÅ¥„Åã„ÇâË¶ã„Åà„Çã„Çà„ÅÜ„Å´„Åô„ÇãÂá¶ÁêÜ
  @JSExportTopLevel("fib", moduleID = "scalamain")
  def fib(n: Int): Int = {
    if (n <= 1) n
    else fib(n - 1) + fib(n - 2)
  }
}





